const carReducer = (state = [], action) => {
    switch (action.type) {
        case 'ADD_CAR':
            return state.concat([action.data]);
        case 'DELETE_CAR':
            return state.filter((car) => car.id !== action.id);
        default:
            return state;
    }
}

export default carReducer;
